package _00_Java_CheatSheet;

public class _00_Primitive_Data_Types {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		System.out.println("Type         Size     Range");
		System.out.println("==========================================");
		
		
		System.out.println("byte           8      -128 to 127");
		
		System.out.println("short          16     -32,768 to 32,767");
		
		System.out.println("int 	       32     -2,147,483,648 to 2,147,483,647");
		
		System.out.println("long 	       64      9,223,372,036,854,775,808 to 9,223..");
		
		System.out.println("float 	       32      3.4e-0.38 to 3.4e+0.38");
		
		System.out.println("double         64      1.7e-308 to 1.7e+308");
		
		System.out.println("char           16      Complete Unicode Character Set");
		
		System.out.println("Boolean         1      True or False");
		
		
		
		
	}

}
