package _00_Java_EMC;

public class _07_01_Nested_For_Loop_Print_3_Star {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		// Print *** 3 times
		
		for (int i = 1; i<=3; i=i+1) {

			for (int j = 1; j<=3; j=j+1) {
				System.out.print("*");
			}
			System.out.println();
			}
		}}
