package _00_Java_IT;

public class _03_Switch_Case {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		char m ='z';
		switch(m) {
		case 'a':
			System.out.println("Monday");
			break;
		case 'b':
			System.out.println("Tuesday");
			break;
		default:
			System.out.println("Enter the value between 1 to 7");
			break;
		}
	}

}
