package _J_02_03_Method;

public class _06_Method_with_Parameter {
	
	
	// Here "int money" is Parameter

	void soap(int money) {
		
		System.out.println("Lifboey");
	}
	
	void paste (int money) {
		
		System.out.println("Himalayas");
	}
	
	public static void main(String[] args) {
		// TODO Auto-generated method stub

		_06_Method_with_Parameter buy = new _06_Method_with_Parameter();
		buy.soap(35);
		buy.paste(60);
	}

}
