package _J_09_Nested_If;

public class _00_Nested_If_EMC {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		// nested if is "IF INSIDE IF"

		Boolean kfc = true;
		Boolean chikcen = true;
		Boolean Coca_Cola = true;

		if (kfc) {
			System.out.println("Going to KFC");
			if (chikcen) {
				System.out.println("Eat Chicken");
				if (Coca_Cola) {
					System.out.println("Drinking Coca Cola");

				}}}}}
