package _J_13_Array;

public class _10_Array_EMC {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		// [] ===> symbol is used to denote ARRAY 
		
		
		String[] Playlist = new String[4];
		
		Playlist[0] = "Song 1";
		Playlist[1] = "Song 2";
		Playlist[2] = "Song 3";
		Playlist[3] = "Song 4";
		
		System.out.println("Playlist[0] = "+Playlist[0]);
		System.out.println("Playlist[1] = "+Playlist[1]);
		System.out.println("Playlist[2] = "+Playlist[2]);
		System.out.println("Playlist[3] = "+Playlist[3]);
		
	}

}
